            }
            public class Status
            {
                public Status()
                {

                }
                public int action { get; set; }
                public int error { get; set; }
            }
            public Data content { get; set; }
            public Status result { get; set; }
            public static SpotInfo FromJson(string jsonStr)
            {
                JavaScriptSerializer jss = new JavaScriptSerializer();
                jss.MaxJsonLength = 2097152;
                SpotInfo result = jss.Deserialize<SpotInfo>(jsonStr);
                return result;
            }
        }
