        public static string GetSpotId(string latY, string lngX)
        {
            string result = "{\"success\": ";//true,\"pid\": 334}"
            string fileName = "Spots.db";
            if(longconn == null)
            {
                longconn = new SQLiteConnection("Data source = " + fileName);
                longconn.Open();
            }
            using (SQLiteCommand cmd = new SQLiteCommand(longconn))
            {
                cmd.CommandText = string.Format("select min(({0} - loc_X) * ({1} - loc_X) + ({2} - loc_Y) * ({3} - loc_Y)), pid from PointInfo;",
                    lngX, lngX, latY, latY);
                try {
                    SQLiteDataReader reader = cmd.ExecuteReader();
                    if (reader.Read())
                    {
                        double distance = reader.GetDouble(0);
                        string pid = reader.GetString(1);
                        result = result + "true,";
                        if (distance < 2500)
                        {
                            result = result + "\"pid\": \"" + pid + "\"}";
                        }
                        else
                        {
                            result = result + "\"pid\": \"\"}";
                        }
                    }
                    else
                    {
                        result = result + "false,\"pid\": \"\"}";
                    }
                }
                catch(Exception e)
                {
                    result = result + "false,\"pid\": null}";
                }
            }
            return result;
        }

