        public static string HttpGetText(string uri)
        {
            HttpWebRequest request = (HttpWebRequest)WebRequest.Create(uri);
            request.Accept = "*/*";
            request.KeepAlive = true;
            request.Method = "GET";

            request.UserAgent = "Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.4 (KHTML, like Gecko) Chrome/22.0.1229.94 Safari/537.4";

            request.Proxy = null;
            request.Timeout = 20000;
            try {
                using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())
                {
                    StreamReader reader = new StreamReader(response.GetResponseStream());
                    string sReturn = reader.ReadToEnd().Trim();
                    return sReturn;
                }
            }
            catch(Exception e)
            {
                return null;
            }
        }
        public static string GetSpotIdOnline(string latY, string lngX)
        {
            string result = "{\"success\": ";
            LatLng baiduLoc = BdCoodOffsetProvider.getInstance().StandardMercator2BaiduMercator(new LatLng(double.Parse(latY), double.Parse(lngX)));
            string url = "http://pcsv0.map.bdimg.com/?qt=qsdata&x=" + baiduLoc.longitude + "&y=" + baiduLoc.latitude;
            string spotInfo = HttpGetText(url);
            if(spotInfo != null)
            {
                SpotInfo info = SpotInfo.FromJson(spotInfo);
                if (info != null)
                {
                    result = result + "true,";
                    LatLng correctLoc = BdCoodOffsetProvider.getInstance().BaiduMercator2StandardMercator(new LatLng(((double)info.content.y) / 100.0, ((double)info.content.x) / 100.0));
                    result = result + "\"pid\": \"" + info.content.id + "\"," + "\"x\": " + correctLoc.longitude + ", \"y\": " + correctLoc.latitude + "}";
                }
                else
                {
                    result = result + "true,\"pid\": null, \"x\":0, \"y\":0}";
                }
            }           
            else
            {
                result = result + "false,\"pid\": null, \"x\":0, \"y\":0}";
            }
            return result;
        }
